#!/bin/bash

# Issue #16 Fix Verification Script
#
# This script tests the orchestrator-agent ping-pong coordination fix
# that addresses the missing step-by-step execution problem.

echo "🧪 Testing Issue #16 Fix: Orchestrator-Agent Ping-Pong Coordination"

# Build the project
echo "📦 Building project..."
cargo build --quiet --bin reev-api --bin reev-runner
if [ $? -ne 0 ]; then
    echo "❌ Build failed - check compilation errors"
    exit 1
fi

echo "✅ Build successful"

# Start API server
echo "🚀 Starting API server..."
pkill -f reev-api 2>/dev/null
RUST_LOG=info cargo run --bin reev-api --quiet > issue_16_test.log 2>&1 &
API_PID=$!
sleep 5

# Check if server started
if ! curl -s http://localhost:3001/api/v1/benchmarks >/dev/null; then
    echo "❌ API server failed to start"
    cat issue_16_test.log
    kill $API_PID 2>/dev/null
    exit 1
fi

echo "✅ API server started (PID: $API_PID)"

# Test 1: Basic ping-pong execution
echo ""
echo "🧪 Test 1: Basic ping-pong coordination"
FLOW_ID=$(curl -s -X POST http://localhost:3001/api/v1/benchmarks/execute-direct \
  -H "Content-Type: application/json" \
  -d '{
    "prompt": "swap 0.5 SOL for USDC",
    "wallet": "test_issue_16_wallet",
    "agent": "glm-4.6-coding",
    "shared_surfpool": false
  }' | jq -r '.execution_id')

echo "📋 Flow ID: $FLOW_ID"

if [ "$FLOW_ID" = "null" ] || [ -z "$FLOW_ID" ]; then
    echo "❌ Test 1 failed - no execution ID"
    tail -20 issue_16_test.log
    kill $API_PID 2>/dev/null
    exit 1
fi

# Wait for execution and get results
sleep 3
TOOL_CALLS=$(curl -s "http://localhost:3001/api/v1/flows/$FLOW_ID" | jq '.metadata.tool_count')

echo "📊 Tool calls executed: $TOOL_CALLS"

# Test 2: Multi-step ping-pong execution
echo ""
echo "🧪 Test 2: Multi-step ping-pong coordination"
FLOW_ID_2=$(curl -s -X POST http://localhost:3001/api/v1/benchmarks/execute-direct \
  -H "Content-Type: application/json" \
  -d '{
    "prompt": "use my 50% sol to multiply usdc 1.5x on jup",
    "wallet": "test_issue_16_wallet",
    "agent": "glm-4.6-coding",
    "shared_surfpool": false
  }' | jq -r '.execution_id')

echo "📋 Multi-step Flow ID: $FLOW_ID_2"

if [ "$FLOW_ID_2" = "null" ] || [ -z "$FLOW_ID_2" ]; then
    echo "❌ Test 2 failed - no execution ID"
    tail -20 issue_16_test.log
    kill $API_PID 2>/dev/null
    exit 1
fi

# Wait for execution and check step-by-step results
sleep 5
STEP_RESULTS=$(curl -s "http://localhost:3001/api/v1/flows/$FLOW_ID_2" | jq '.metadata')

echo "📊 Multi-step results: $STEP_RESULTS"

# Test 3: Verify ping-pong coordination in logs
echo ""
echo "🧪 Test 3: Verify ping-pong coordination in logs"
PING_PONG_LOGS=$(grep -c "ping-pong" issue_16_test.log 2>/dev/null || echo "0")
STEP_EXECUTION_LOGS=$(grep -c "Executing step" issue_16_test.log 2>/dev/null || echo "0")

echo "📋 Ping-pong coordination mentions: $PING_PONG_LOGS"
echo "📋 Step execution mentions: $STEP_EXECUTION_LOGS"

# Clean up
kill $API_PID 2>/dev/null

# Results summary
echo ""
